(221, 37, 'headline', 'Kalmar Cykelklubb'),
(222, 37, '_headline', 'field_599be26be3e25'),
(223, 37, 'second_headline', 'Bli en del av vår gemenskap'),
(224, 37, '_second_headline', 'field_599be2d7e3e26'),
(225, 37, 'headline_upptack', 'Upptäck'),
(226, 37, '_headline_upptack', 'field_599be9c7732c3'),
(227, 37, 'second_headline_upptack', 'Kalmar äldsta cykelklubb'),
(228, 37, '_second_headline_upptack', 'field_599be9f5732c4'),
(229, 37, 'first_paragraph_upptack', 'Våra aktiviteter handlar i huvudsak om motions cykling på landsväg eller i terräng och spinning under vinter halvåret.'),
(230, 37, '_first_paragraph_upptack', 'field_599bea34732c5'),
(231, 37, 'second_paragraph_upptack', 'Det viktigaste för oss är uteträningen med motionslopp vår-sommar-höst och inneträningen vintertid. Våra fokusområden är Ironman, Vätternrundan, GP-loppet och träningsrundor.'),
(232, 37, '_second_paragraph_upptack', 'field_599bea5f732c7'),
(233, 37, 'third_paragraph_upptack', 'Vi har ett stort uppdrag i att sköta cykelmomentet vid Ironman vi har även trevligt ihop i form av aktiviteter utanför cyklingen t.ex. mek. kvällar, friluftsträffar med grillning, bowling med mat, sånt som du som medlem kan vara med och påverka'),
(234, 37, '_third_paragraph_upptack', 'field_599bea5e732c6'),
(235, 37, 'headline_pa_gang', 'På gång hos oss'),
(236, 37, '_headline_pa_gang', 'field_599bec58d09dc'),
