(239, 37, 'headline_verksamhet', 'Verksamhet'),
(240, 37, '_headline_verksamhet', 'field_599c97b99ae09'),
(241, 37, 'second_headline_verksamhet', 'Vår idé är att vara en cykelklubb som samlar likasinnade till att utöva cykling som motionsform.'),
(242, 37, '_second_headline_verksamhet', 'field_599c97cb9ae0a'),
(243, 37, 'bild_verksamhet', '27'),
(244, 37, '_bild_verksamhet', 'field_599c97dc9ae0b'),
(245, 37, 'first_paragraph_verksamhet', 'Vi lär ut grunderna för gruppcykling för att kunna ha roligt tillsammans'),
(246, 37, '_first_paragraph_verksamhet', 'field_599c97f29ae0c'),
(247, 37, 'second_paragraph_verksamhet', 'Vi riktar oss till dig som nybörjarcyklist, motionär, glad amatör och cykel intresserad'),
