(2165, 168, 'headline_verksamhet', 'Verksamhet'),
(2166, 168, '_headline_verksamhet', 'field_599c97b99ae09'),
(2167, 168, 'second_headline_verksamhet', 'Vår idé är att vara en cykelklubb som samlar likasinnade till att utöva cykling som motionsform.'),
(2168, 168, '_second_headline_verksamhet', 'field_599c97cb9ae0a'),
(2169, 168, 'bild_verksamhet', '27'),
(2170, 168, '_bild_verksamhet', 'field_599c97dc9ae0b'),
(2171, 168, 'first_paragraph_verksamhet', 'Vi lär ut grunderna för gruppcykling för att kunna ha roligt tillsammans'),
(2172, 168, '_first_paragraph_verksamhet', 'field_599c97f29ae0c'),
(2173, 168, 'second_paragraph_verksamhet', 'Vi riktar oss till dig som nybörjarcyklist, motionär, glad amatör och cykel intresserad'),
